import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [formData, setFormData] = useState({
    FirstName: "",
    LastName: "",
    Gender: "",
    DoB: "",
    Email: "",
    Mobile: "",
    location: "Chennai",
  });
  const [total, setTotal] = useState([]);
  // const [table, setTable] = useState(false);
  useEffect(() => {
    const savedData = JSON.parse(localStorage.getItem("DETAIL FORM") || "[]");
    setTotal(savedData);
  }, []);
  const saveForm = (details) => {
    localStorage.setItem("DETAIL FORM", JSON.stringify(details));
  };
  const submitData = () => {
    let datas = [...total, formData];
    setTotal(datas);
    saveForm(datas);

    setTable(true);

    setFormData({
      FirstName: "",
      LastName: "",
      Gender: "",
      DoB: "",
      Email: "",
      Mobile: "",
      location: "",
    });
  };

  return (
    <div className="App">
      <label>
        FirstName :
        <input
          type="text"
          name="FirstName"
          placeholder="First Name"
          value={formData.FirstName}
          onChange={(e) =>
            setFormData({ ...formData, FirstName: e.target.value })
          }
        />
      </label>
      <label>
        LastName :
        <input
          type="text"
          name="LastName"
          value={formData.LastName}
          placeholder="Last Name"
          onChange={(e) =>
            setFormData({ ...formData, LastName: e.target.value })
          }
        />
      </label>
      <label>
        Gender :
        <input
          type="radio"
          value="Male"
          name="Gender"
          checked={formData.Gender === "Male"}
          onChange={(e) => setFormData({ ...formData, Gender: e.target.value })}
        />
        Male
        <input
          type="radio"
          value="Female"
          name="Gender"
          checked={formData.Gender === "Female"}
          onChange={(e) => setFormData({ ...formData, Gender: e.target.value })}
        />
        Female
        <input
          type="radio"
          value="Others"
          name="Gender"
          checked={formData.Gender === "Others"}
          onChange={(e) => setFormData({ ...formData, Gender: e.target.value })}
        />
        Others
      </label>
      <label>
        Date Of Brith :
        <input
          type="date"
          name="DoB"
          value={formData.DoB}
          onChange={(e) => setFormData({ ...formData, DoB: e.target.value })}
        />
      </label>
      <label>
        {" "}
        Mail ID :
        <input
          type="email"
          name="Email"
          placeholder="xyz@gmail.com"
          value={formData.Email}
          onChange={(e) => setFormData({ ...formData, Email: e.target.value })}
        />
      </label>
      <label>
        {" "}
        Mobile No :
        <input
          type="number"
          name="Mobile"
          placeholder="9876543210"
          value={formData.Mobile}
          onChange={(e) => setFormData({ ...formData, Mobile: e.target.value })}
        />
      </label>
      <label>
        Location :
        <select
          name="location"
          value={formData.location}
          onChange={(e) => {
            console.log(e.target.value);
            setFormData({ ...formData, location: e.target.value });
          }}
        >
          <option value="Chennai">Chennai</option>
          <option value="Bangalore">Bangalore</option>
          <option value="Coimbatore">Coimbatore</option>
        </select>
      </label>
      <button onClick={submitData}> SUBMIT</button>

      <div>
        <table>
          {console.log(total)}
          {total.lenght > 0 && (
            <tr>
              <th>SI.NO</th>
              <th>Name</th>
              <th>Gender</th>
              <th>DoB</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>location</th>
            </tr>
          )}
          {total.map((totals, index) => (
            <tr>
              <td>{index + 1}</td>
              <td>
                {totals.FirstName}
                {totals.LastName}
              </td>
              <td>{totals.Gender}</td>
              <td>{totals.DoB}</td>
              <td>{totals.Email}</td>
              <td>{totals.Mobile}</td>
              <td>{totals.location}</td>
            </tr>
          ))}
        </table>
      </div>
    </div>
  );
}

export default App;
